import React, {Component} from 'react';
import './App.css';
import List from './List';
import Form from './Form';

class App extends Component {
  state = {
    tasks: []
  }

  handleSubmit = (task) => {
    this.setState({tasks: [...this.state.tasks, task]})
  }
  render() {
    const {tasks} = this.state;

    return (
      <div className="App">
        <h1>Simple To Do App!</h1>
        <Form />
        <List taskList={tasks} />
      </div>
    )
  }
}

export default App;
