import React, {Component} from 'react';
import './App.css';
import List from './List';

class App extends Component {
  state = {
    tasks: [
      {title: "Wash dishes"},
      {title: "Learn React"},
    ]
  }
  render() {
    const {tasks} = this.state
    return (
      <div className="App">
        <h1>Simple To Do App!</h1>
        <List taskList={tasks} />
      </div>
    )
  }
}

export default App;
